import {LibBadgeFacet} from "../lib/LibBadgeFacet.sol";
import {LibCoreFacet} from "../lib/LibCoreFacet.sol";
import {NFTBadge} from "../NFTBadge.sol";
import {Counters} from "@openzeppelin/contracts/utils/Counters.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract BadgeFacet {
    using Counters for Counters.Counter;
    using SafeMath for uint256;

    event BadgeCreated(LibBadgeFacet.BadgeData badgeData,string nftSymbol, string nftInitBaseURI,uint256 id);

    function addBadge(
        LibBadgeFacet.BadgeData memory _badgeData,
        string calldata _nftSymbol,
        string calldata _nftInitBaseURI
    ) external {
        LibBadgeFacet.BadgeState storage ds = LibBadgeFacet.diamondStorage();
        LibCoreFacet.CoreState storage cds = LibCoreFacet.diamondStorage();
        ds.badgeIds.increment();
        LibCoreFacet.enforceGroupOwner(_badgeData.groupId, msg.sender);
        uint256[3] memory requiredQuests;
        for (uint256 i = 0; i < _badgeData.requiredQuests.length; i++) {
            if (_badgeData.requiredQuests[i] > 0) {
                LibCoreFacet.enforceOwnerOfQuest(
                    _badgeData.requiredQuests[i],
                    msg.sender
                );
            }
            requiredQuests[i] = _badgeData.requiredQuests[i];
        }
        ds.badgesById[ds.badgeIds.current()].requiredQuests = requiredQuests;
        ds.badgesById[ds.badgeIds.current()].engagePointsThreshold = _badgeData
            .engagePointsThreshold;
        ds.badgesById[ds.badgeIds.current()].badgePrice = _badgeData.badgePrice;
        ds.badgesById[ds.badgeIds.current()].name = _badgeData.name;
        ds.badgesById[ds.badgeIds.current()].groupId = _badgeData.groupId;
        ds.badgesById[ds.badgeIds.current()].NFT = address(
            new NFTBadge(_badgeData.name, _nftSymbol, _nftInitBaseURI)
        );

        //store id of badge for group
        uint256 mainBadgeOfGroup = ds.mainBadgeOfGroup[_badgeData.groupId];
        if (mainBadgeOfGroup == 0) {
            ds.mainBadgeOfGroup[_badgeData.groupId] = ds.badgeIds.current();
        }

        emit BadgeCreated(ds.badgesById[ds.badgeIds.current()], _nftSymbol,_nftInitBaseURI,ds.badgeIds.current());
    }

    //TODO
    function claimBadge(uint256 _badgeId) external payable {
        LibBadgeFacet.BadgeState storage ds = LibBadgeFacet.diamondStorage();
        LibCoreFacet.CoreState storage cds = LibCoreFacet.diamondStorage();

        //check if requiredQuests
        uint256[3] memory requiredQuests = ds
            .badgesById[_badgeId]
            .requiredQuests;
        for (uint256 i = 0; i < requiredQuests.length; i++) {
            if (requiredQuests[i] != 0) {
                require(
                    cds.isCompletedByUser[requiredQuests[i]][msg.sender],
                    "user has not complete all quests"
                );
            }
        }
        uint256 engageThreshold = ds.badgesById[_badgeId].engagePointsThreshold;
        if (engageThreshold > 0) {
            require(
                cds.userEngagePoints[ds.badgesById[_badgeId].groupId][
                    msg.sender
                ] >= engageThreshold,
                "user doesnt have enough engage points to claim this badge"
            );
        }
        uint256 badgePrice = ds.badgesById[_badgeId].badgePrice;
        if (badgePrice > 0) {
            require(
                msg.value >= badgePrice,
                "not enought ether to obtain this badge"
            );
        }
        NFTBadge(ds.badgesById[_badgeId].NFT).mint(msg.sender);
    }

    function isMemberOfGroup(uint256 _groupId) external view returns (bool) {
        return (LibBadgeFacet.isMemberOfGroup(msg.sender, _groupId));
    }

    function getBadge(uint256 _badgeId)
        external
        view
        returns (LibBadgeFacet.BadgeData memory)
    {
        LibBadgeFacet.BadgeState storage ds = LibBadgeFacet.diamondStorage();
        return (ds.badgesById[_badgeId]);
    }
}
","keccak256":"0x74c4c69ddecc5cd3a4e394772d5010c044021ff11af1f1a3f224cdc69424f2d8"},"contracts/lib/LibBadgeFacet.sol":{"content":"pragma solidity ^0.8.0;

import {Counters} from "@openzeppelin/contracts/utils/Counters.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

library LibBadgeFacet {
    using Counters for Counters.Counter;

    struct BadgeData {
        uint256[3] requiredQuests;
        uint256 engagePointsThreshold;
        uint256 badgePrice; // ether
        string name;
        address NFT;
        uint256 groupId;
    }
    using Counters for Counters.Counter;
    bytes32 constant DIAMOND_STORAGE_POSITION =
        keccak256("diamond.standard.diamond.storage.badge");
    struct BadgeState {
        Counters.Counter badgeIds;
        mapping(uint256 => BadgeData) badgesById;
        mapping(uint256 => uint256) mainBadgeOfGroup;
    }

    function diamondStorage() internal pure returns (BadgeState storage ds) {
        bytes32 position = DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function isMemberOfGroup(address _user, uint256 _groupId)
        internal
        view
        returns (bool)
    {
        require(
            IERC721(
                diamondStorage()
                    .badgesById[diamondStorage().mainBadgeOfGroup[_groupId]]
                    .NFT
            ).balanceOf(_user) > 0,
            "User doesnt hold main badge of this community"
        );
        return true;
    }
}
","keccak256":"0xd34a6fbf46c98b38292385a4f3d6441d7b66277e9d6247e1d6c41a4d23a86469"},"contracts/lib/LibCoreFacet.sol":{"content":"pragma solidity ^0.8.0;

import {Counters} from "@openzeppelin/contracts/utils/Counters.sol";

library LibCoreFacet {
    struct GroupData {
        bytes32 name;
        address owner;
    }
    struct QuestData {
        bytes32 name;
        uint256 groupId;
        uint256 engagePoints;
    }

    using Counters for Counters.Counter;
    bytes32 constant DIAMOND_STORAGE_POSITION =
        keccak256("diamond.standard.diamond.storage.core");
    struct CoreState {
        Counters.Counter groupIds;
        Counters.Counter questIds; // PolygonId Requests are the same 1 to 1
        mapping(uint256 => GroupData) groupById;
        mapping(uint256 => QuestData) questById;
        mapping(uint256 => mapping(address => bool)) isCompletedByUser; // with quest id
        mapping(uint256 => mapping(address => uint256)) userEngagePoints;
    }

    function diamondStorage() internal pure returns (CoreState storage ds) {
        bytes32 position = DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function enforceGroupOwner(uint256 _groupId, address _owner) internal view {
        require(_groupId != 0, "group id cannot be 0");
        require(_owner != address(0), "owner address cannot be 0");
        require(
            diamondStorage().groupById[_groupId].owner == _owner,
            "caller is not the owner of the group"
        );
    }

    function enforceOwnerOfQuest(uint256 _questId, address _owner)
        internal
        view
    {
        require(_owner != address(0), "owner address cannot be 0");
        require(
            diamondStorage()
                .groupById[diamondStorage().questById[_questId].groupId]
                .owner == _owner,
            "caller is not the owner of the group"
        );
    }
}
