pragma solidity ^0.8.0;

interface ICircuitValidator {
    struct CircuitQuery {
        uint256 schema;
        uint256 slotIndex;
        uint256 operator;
        uint256[] value;
        string circuitId;
    }

    function verify(
        uint256[] memory inputs,
        uint256[2] memory a,
        uint256[2][2] memory b,
        uint256[2] memory c,
        CircuitQuery memory query
    ) external view returns (bool r);

    function getCircuitId() external pure returns (string memory id);

    function getChallengeInputIndex() external pure returns (uint256 index);

    function getUserIdInputIndex() external pure returns (uint256 index);
}","keccak256":"0x922df071c1096ec4eb5a150b42189902ebea179d6a86d0e8616a66db1ecc6db6","license":"MIT"},"contracts/interfaces/IZKPVerifier.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ICircuitValidator.sol";

interface IZKPVerifier {
    function submitZKPResponse(
        uint64 requestId,
        uint256[] memory inputs,
        uint256[2] memory a,
        uint256[2][2] memory b,
